mapper.select:
select atcd.id, atcd.appId, atcd.generalCaseId, atcd.priority, atcd.apiId, atcd.name, atcd.header, atcd.steps, atcd.parameters, atcd.querySql, atcd.expectedResult, atcd.resultCheckMode, atcd.dbConnId, atcd.enabled, atcd.deleted, atcd.createBy, atcd.createTime, atcd.modifiedBy, atcd.modifiedTime
from  apiTestCaseDetails atcd
where 1 = 1
 and atcd.id = #{criteria.id} 
 and atcd.appId = #{criteria.appId} 
 and atcd.generalCaseId = #{criteria.generalCaseId} 
 and atcd.priority = #{criteria.priority} 
 and atcd.apiId = #{criteria.apiId} 
 and atcd.name like concat('%', #{criteria.name}, '%')
 and atcd.resultCheckMode = #{criteria.resultCheckMode} 
 and atcd.dbConnId = #{criteria.dbConnId} 

    and atcd.apiId in

        #{caseId}



     and atcd.enabled = 0 
     and atcd.enabled = 1 



     and atcd.deleted = 1  
    and atcd.deleted = 0

select atcd.id, atcd.appId,  atcd.generalCaseId, atcd.priority, atcd.apiId, atcd.name, atcd.header, atcd.steps, atcd.parameters, atcd.querySql, atcd.expectedResult, atcd.resultCheckMode, atcd.dbConnId, atcd.enabled, atcd.deleted, atcd.createBy, atcd.createTime, atcd.modifiedBy, atcd.modifiedTime
from  apiTestCaseDetails atcd
where 1 = 1
 and atcd.id = #{criteria.id} 
 and atcd.appId = #{criteria.appId} 
 and atcd.generalCaseId = #{criteria.generalCaseId} 
 and atcd.priority = #{criteria.priority} 
 and atcd.apiId = #{criteria.apiId} 
 and atcd.name like concat('%', #{criteria.name}, '%')
 and atcd.resultCheckMode = #{criteria.resultCheckMode} 
 and atcd.dbConnId = #{criteria.dbConnId} 

    and atcd.apiId in

        #{caseId}



     and atcd.enabled = 0 
     and atcd.enabled = 1 



     and atcd.deleted = 1  
    and atcd.deleted = 0 


limit #{offset}, #{capacity}

select count(atcd.id)
from  apiTestCaseDetails atcd
where 1 = 1
 and atcd.id = #{criteria.id} 
 and atcd.appId = #{criteria.appId} 
 and atcd.generalCaseId = #{criteria.generalCaseId} 
 and atcd.priority = #{criteria.priority} 
 and atcd.apiId = #{criteria.apiId} 
 and atcd.name like concat('%', #{criteria.name}, '%')
 and atcd.resultCheckMode = #{criteria.resultCheckMode} 
 and atcd.dbConnId = #{criteria.dbConnId} 

    and atcd.apiId in

        #{caseId}



     and atcd.enabled = 0 
     and atcd.enabled = 1 



     and atcd.deleted = 1  
    and atcd.deleted = 0

mapper.update:
update apiTestCaseDetails
set enabled = #{enabled}
where 1= 1
 and id = #{id}

update apiTestCaseDetails
set expectedResult = null 
where id = #{id}

update apiTestCaseDetails
set dbConnId = null, querySql = null
where id = #{id}

update apiTestCaseDetails
set header = '{}'
where id = #{id}
