mapper.select:
select tci.id, tci.appId, tci.name, tci.url, tci.userName, tci.password, tci.header, tci.enabled, tci.deleted, tci.createBy, tci.createTime, tci.modifiedBy, tci.modifiedTime
from  testDBConnectionInfo tci
where 1 = 1
 and tci.id = #{id} 
 and tci.appId = #{appId} 
 and tci.name like concat('%', #{name}, '%')
 and tci.url = #{url}
 and tci.userName = #{userName}

     and tci.enabled = 0 
     and tci.enabled = 1 



     and tci.deleted = 1  
    and tci.deleted = 0

select tci.id, tci.appId, tci.name, tci.url, tci.userName, tci.password, tci.header, tci.enabled, tci.deleted, tci.createBy, tci.createTime, tci.modifiedBy, tci.modifiedTime
from  testDBConnectionInfo tci
where 1 = 1
 and tci.id = #{connInfo.id} 
 and tci.appId = #{connInfo.appId} 
 and tci.name like concat('%', #{connInfo.name}, '%')
 and tci.url = #{connInfo.url}
 and tci.userName = #{connInfo.userName}

     and tci.enabled = 0 
     and tci.enabled = 1 



     and tci.deleted = 1  
    and tci.deleted = 0 


limit #{offset}, #{capacity}

select count(tci.id)
from  testDBConnectionInfo tci
where 1 = 1
 and tci.id = #{id} 
 and tci.appId = #{appId} 
 and tci.name like concat('%', #{name}, '%')
 and tci.url = #{url}
 and tci.userName = #{userName}

     and tci.enabled = 0 
     and tci.enabled = 1 



     and tci.deleted = 1  
    and tci.deleted = 0

mapper.update:
update testDBConnectionInfo
set deleted = 1
where 1= 1
 and id = #{id} 
 and appId = #{appId}

update testDBConnectionInfo
set enabled = #{enabled}
where 1= 1
 and id = #{id} 
 and appId = #{appId}
