mapper.select:
select tp.id, tp.appId, tp.moduleId,  tp.functionId, tp.name, tp.testType, tp.priority, tp.repeatFlag, tp.triggerTime, tp.cron, tp.caseSet, tp.caseSize, tp.enabled, tp.deleted, tp.createBy, tp.createTime, tp.modifiedBy, tp.modifiedTime
from  testPlan tp
where 1 = 1
 and tp.id = #{criteria.id} 
 and tp.appId = #{criteria.appId} 
 and tp.moduleId = #{criteria.moduleId} 
 and tp.functionId = #{criteria.functionId} 
 and tp.name like concat('%', #{criteria.name}, '%')
 and tp.testType = #{criteria.testType} 
 and tp.priority = #{criteria.priority} 
 and tp.repeatFlag = #{criteria.repeatFlag} 

     and tp.enabled = 0 
     and tp.enabled = 1 



     and tp.deleted = 1  
    and tp.deleted = 0

select tp.id, tp.appId, tp.moduleId,  tp.functionId, tp.name, tp.testType, tp.priority, tp.repeatFlag, tp.triggerTime, tp.cron, tp.caseSet, tp.caseSize, tp.enabled, tp.deleted, tp.createBy, tp.createTime, tp.modifiedBy, tp.modifiedTime
from  testPlan tp
where tp.triggerTime  <=  #{criteria.triggerTime} and tp.repeatFlag = #{criteria.repeatFlag}

select tp.id, tp.appId, tp.moduleId,  tp.functionId, tp.name, tp.testType, tp.priority, tp.repeatFlag, tp.triggerTime, tp.cron, tp.caseSet, tp.caseSize, tp.enabled, tp.deleted, tp.createBy, tp.createTime, tp.modifiedBy, tp.modifiedTime
from  testPlan tp
where tp.id = #{id}

select tp.id, tp.appId,  tp.moduleId,  tp.functionId, tp.name, tp.testType, tp.priority, tp.repeatFlag, tp.triggerTime, tp.cron, tp.caseSet, tp.caseSize, tp.enabled, tp.deleted, tp.createBy, tp.createTime, tp.modifiedBy, tp.modifiedTime
from  testPlan tp
where 1 = 1
 and tp.id = #{criteria.id} 
 and tp.appId = #{criteria.appId} 
 and tp.moduleId = #{criteria.moduleId} 
 and tp.functionId = #{criteria.functionId} 
 and tp.name like concat('%', #{criteria.name}, '%')
 and tp.testType = #{criteria.testType} 
 and tp.priority = #{criteria.priority} 
 and tp.repeatFlag = #{criteria.repeatFlag} 

     and tp.enabled = 0 
     and tp.enabled = 1 



     and tp.deleted = 1  
    and tp.deleted = 0 


limit #{offset}, #{capacity}

select count(tp.id)
from  testPlan tp
where 1 = 1
 and tp.id = #{criteria.id} 
 and tp.appId = #{criteria.appId} 
 and tp.moduleId = #{criteria.moduleId} 
 and tp.functionId = #{criteria.functionId} 
 and tp.name like concat('%', #{criteria.name}, '%')
 and tp.testType = #{criteria.testType} 
 and tp.priority = #{criteria.priority} 
 and tp.repeatFlag = #{criteria.repeatFlag} 

     and tp.enabled = 0 
     and tp.enabled = 1 



     and tp.deleted = 1  
    and tp.deleted = 0

mapper.update:
update testPlan
set deleted = 1
where id = #{id}

update testPlan
set enabled = #{enabled}
where id = #{id}
